SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_atualizar_via_classif ( nm_usuario_p text) AS $body$
DECLARE


qt_reg_w		bigint;
ds_forma_w		varchar(100);
nr_seq_forma_w		bigint;
vl_dominio_w		varchar(10);

C01 CURSOR FOR
	SELECT	nr_sequencia,
		DS_FORMA_PAGAMENTO
	from	via_forma_pagamento
	order by 1;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	user_tab_columns
where 	TABLE_NAME	= 'VIA_CLASSIF_DESP'
and	column_name	= 'IE_FORMA';

if (qt_reg_w > 0) then

	select	count(*)
	into STRICT	qt_reg_w
	from	via_forma_pagamento;

	if (qt_reg_w	> 0) then

		open C01;
		loop
		fetch 	C01 into
			nr_seq_forma_w,
			ds_forma_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin

			select	vl_dominio
			into STRICT	vl_dominio_w
			from	valor_dominio
			where	cd_dominio		= 1694
			and	ds_valor_dominio	= ds_forma_w;

			CALL Exec_Sql_Dinamico('Ricardo', 'update	via_classif_desp ' ||
						     ' set	NR_SEQ_FORMA_PAGTO	= ' || nr_seq_forma_w ||
						     ' where	ie_forma		= '|| chr(39) || vl_dominio_w|| chr(39));

			CALL Exec_Sql_Dinamico('Ricardo', 'update	via_classif_desp_cp ' ||
						     ' set	NR_SEQ_FORMA_PAGTO	= ' || nr_seq_forma_w ||
						     ' where	ie_forma		= '|| chr(39) || vl_dominio_w || chr(39));

			end;
		end loop;
		close C01;


	end if;
end if;

select	count(*)
into STRICT	qt_reg_w
from	via_classif_desp
where 	coalesce(NR_SEQ_FORMA_PAGTO::text, '') = '';

if (qt_reg_w	= 0) then

	CALL Exec_Sql_Dinamico('Ricardo', 'alter table via_classif_desp modify (NR_SEQ_FORMA_PAGTO number(10,0) not null) ');
	CALL Exec_Sql_Dinamico('Ricardo', 'alter table via_classif_desp drop column ie_forma ');

end if;

select	count(*)
into STRICT	qt_reg_w
from	via_classif_desp_cp
where 	coalesce(NR_SEQ_FORMA_PAGTO::text, '') = '';

if (qt_reg_w	= 0) then

	CALL Exec_Sql_Dinamico('Ricardo', 'alter table via_classif_desp_cp modify (NR_SEQ_FORMA_PAGTO number(10,0) not null) ');
	CALL Exec_Sql_Dinamico('Ricardo', 'alter table via_classif_desp_cp drop column ie_forma ');

end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
