SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_atualizar_via_desp ( nm_usuario_p text) AS $body$
DECLARE


qt_reg_w		bigint;
vl_dominio_w		varchar(20);
ds_valor_dominio_w	varchar(100);
nr_seq_forma_w		bigint;

C01 CURSOR FOR
	SELECT	vl_dominio,
		ds_valor_dominio
	from	valor_dominio
	where	cd_dominio	= 1694
	order by 1;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	user_tab_columns
where 	TABLE_NAME	= 'VIA_DESPESA'
and	column_name	= 'IE_FORMA';

if (qt_reg_w > 0) then

	select	count(*)
	into STRICT	qt_reg_w
	from	via_forma_pagamento;

	if (qt_reg_w	= 0) then

		open C01;
		loop
		fetch 	C01 into
			vl_dominio_w,
			ds_valor_dominio_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin

			select	nextval('via_forma_pagamento_seq')
			into STRICT	nr_seq_forma_w
			;

			insert 	into via_forma_pagamento(NR_SEQUENCIA           ,
				DT_ATUALIZACAO         ,
				NM_USUARIO             ,
				DT_ATUALIZACAO_NREC    ,
				NM_USUARIO_NREC        ,
				DS_FORMA_PAGAMENTO     ,
				IE_PAGADOR )
			values (nr_seq_forma_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				ds_valor_dominio_w,
				'E');

			CALL Exec_Sql_Dinamico('Ricardo', 'update	via_despesa ' ||
						     ' set	NR_SEQ_FORMA_PAGTO	= ' || nr_seq_forma_w ||
						     ' where	ie_forma		= '|| chr(39) || vl_dominio_w || chr(39));


			end;
		end loop;
		close C01;


	end if;
end if;

select	count(*)
into STRICT	qt_reg_w
from	via_despesa
where 	coalesce(NR_SEQ_FORMA_PAGTO::text, '') = '';

if (qt_reg_w	= 0) then

	CALL Exec_Sql_Dinamico('Ricardo', 'alter table via_despesa modify (NR_SEQ_FORMA_PAGTO number(10,0) not null) ');
	CALL Exec_Sql_Dinamico('Ricardo', 'alter table via_despesa drop column ie_forma ');

end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baca_atualizar_via_desp ( nm_usuario_p text) FROM PUBLIC;

