CREATE OR REPLACE PROCEDURE baca_atualiza_calcula_peona ( cd_estabelecimento_p bigint, dt_mes_inicio_p timestamp, dt_mes_fim_p timestamp) AS $body$
DECLARE


nr_seq_peona_w			bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_peona
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	trunc(dt_competencia, 'month') between trunc(dt_mes_inicio_p, 'month') and trunc(dt_mes_fim_p, 'month')
	order by dt_competencia;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_peona_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	CALL pls_calcular_peona(nr_seq_peona_w, 'Baca');
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
