SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_atualiza_fim_etapa_conta () AS $body$
DECLARE


nr_seq_etapa_w		bigint;
nr_interno_conta_w	bigint;
nr_interno_conta_ant_w	bigint;
dt_inicio_etapa_w	timestamp;
dt_final_w		timestamp;

c01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.nr_interno_conta,
		a.dt_etapa
	from	fatur_etapa c,
		conta_paciente_etapa a,
		conta_paciente b
	where	coalesce(a.dt_fim_etapa::text, '') = ''
	and 	a.nr_interno_conta	= b.nr_interno_conta
	and	a.nr_seq_etapa		= c.nr_sequencia
	and	c.ie_finalizar_etapa	= 'A'
	and	b.ie_status_acerto	= 1
	and	coalesce(c.ie_situacao,'A')	= 'A'
	order by 2, 3 desc;


BEGIN

nr_interno_conta_ant_w	:= 0;
dt_final_w		:= null;

open	c01;
loop
fetch	c01 into
	nr_seq_etapa_w,
	nr_interno_conta_w,
	dt_inicio_etapa_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	if (nr_interno_conta_ant_w	<> nr_interno_conta_w) then
		dt_final_w	:= null;
	end if;

	if (dt_final_w IS NOT NULL AND dt_final_w::text <> '') then
		update	conta_paciente_etapa
		set	dt_fim_etapa		= dt_final_w
		where	nr_sequencia		= nr_seq_etapa_w;
	end if;


	nr_interno_conta_ant_w	:= nr_interno_conta_w;
	dt_final_w		:= dt_inicio_etapa_w;

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baca_atualiza_fim_etapa_conta () FROM PUBLIC;

