SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_atualiza_medico_cred ( nm_usuario_p text) AS $body$
DECLARE


cd_medico_w			varchar(10);
ie_conveniado_w			varchar(01);
ie_auditor_w			varchar(01);
ie_tipo_servico_sus_w		varchar(03);
qt_reg_w			integer;
nr_sequencia_w			bigint;


c01 CURSOR FOR
	SELECT	a.cd_pessoa_fisica,
		a.ie_conveniado,
		a.ie_auditor,
		a.IE_TIPO_SERVICO_SUS
	from	medico_convenio a
	where	(ie_tipo_servico_sus IS NOT NULL AND ie_tipo_servico_sus::text <> '');



BEGIN

select	count(*)
into STRICT	qt_reg_w
from	SUS_MEDICO_CREDENCIAMENTO;

if (qt_reg_w	= 0) then
	begin

	open C01;
	loop
	fetch C01 into
			cd_medico_w,
			ie_conveniado_w,
			ie_auditor_w,
			ie_tipo_servico_sus_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select	count(*)
		into STRICT	qt_reg_w
		from	SUS_MEDICO_CREDENCIAMENTO
		where	cd_medico	= cd_medico_w;

		if (qt_reg_w	= 0) then

			select	nextval('sus_medico_credenciamento_seq')
			into STRICT	nr_sequencia_w
			;

			insert	into SUS_MEDICO_CREDENCIAMENTO(NR_SEQUENCIA,
				CD_MEDICO,
				IE_CREDENCIAMENTO,
				DT_ATUALIZACAO,
				NM_USUARIO,
				DT_ATUALIZACAO_NREC,
				NM_USUARIO_NREC,
				IE_CONVENIADO,
				IE_AUDITOR)
			values (nr_sequencia_w,
				cd_medico_w,
				ie_tipo_servico_sus_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				ie_conveniado_w,
				ie_auditor_w);

		end if;

		end;
	end loop;
	close C01;


	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baca_atualiza_medico_cred ( nm_usuario_p text) FROM PUBLIC;

