SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_atualiza_projeto_rat ( nr_seq_cliente_p bigint) AS $body$
DECLARE


nr_seq_rat_w			bigint;
nr_seq_cliente_w		bigint;
nr_seq_projeto_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_cliente
	from	proj_rat
	where	coalesce(nr_seq_proj::text, '') = ''
	and	((nr_seq_cliente = nr_seq_cliente_p) or (coalesce(nr_seq_cliente_p,0) = 0));


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_rat_w,
	nr_seq_cliente_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_projeto_w
	from	proj_projeto
	where	nr_seq_cliente	= nr_seq_cliente_w;

	if (nr_seq_projeto_w	> 0) then
		update	proj_rat
		set	nr_seq_proj	= nr_seq_projeto_w,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia	= nr_seq_rat_w;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
