CREATE OR REPLACE PROCEDURE baca_atualiza_vl_pat_ant ( dt_mesano_referencia_p timestamp) AS $body$
DECLARE



nr_sequencia_w		bigint;
nr_seq_bem_w		bigint;
vl_base_deprec_mes_ant_w	double precision;
vl_deprec_acum_mes_ant_w	double precision;

c01 CURSOR FOR
SELECT	b.nr_sequencia,
	b.nr_seq_bem
from	pat_valor_bem b
where 	trunc(b.dt_valor,'mm') = trunc(dt_mesano_referencia_p,'mm');



BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	nr_seq_bem_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	coalesce(sum(vl_base_deprec),0),
		coalesce(sum(vl_deprec_acum),0)
	into STRICT	vl_base_deprec_mes_ant_w,
		vl_deprec_acum_mes_ant_w
	from	pat_valor_bem
	where	nr_seq_bem = nr_seq_bem_w
	and	trunc(dt_valor,'mm') = trunc(add_months(dt_mesano_referencia_p,-1),'mm');

	update	pat_valor_bem
	set	vl_base_deprec_ant	= vl_base_deprec_mes_ant_w,
		vl_deprec_acum_ant	= vl_deprec_acum_mes_ant_w
	where	nr_sequencia		= nr_sequencia_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
