SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_atual_natureza_sped () AS $body$
DECLARE


ie_natureza_sped_w		varchar(15);

/* 4696 SPED - Natureza da conta/grupo de contas
*/
c01 CURSOR FOR
SELECT	distinct
	a.cd_grupo,
	a.ie_tipo
from	ctb_grupo_conta a,
	conta_contabil b
where	b.cd_grupo	= a.cd_grupo
and	coalesce(b.ie_natureza_sped,'X') = 'X'
order by 1;

vet01	c01%RowType;


BEGIN

open C01;
loop
fetch C01 into
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (vet01.ie_tipo = 'A') then
		ie_natureza_sped_w	:= '01';
	elsif (vet01.ie_tipo = 'P') then
		ie_natureza_sped_w	:= '02';
	elsif (vet01.ie_tipo in ('R','C','D')) then
		ie_natureza_sped_w	:= '04';
	else
		ie_natureza_sped_w	:= '09';
	end if;

	update	conta_contabil
	set	ie_natureza_sped	= ie_natureza_sped_w
	where	cd_grupo		= vet01.cd_grupo
	and	coalesce(ie_natureza_sped,'X') = 'X';

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baca_atual_natureza_sped () FROM PUBLIC;

