CREATE OR REPLACE PROCEDURE baca_baixar_exame_lab (dt_parametro_p timestamp, cd_motivo_baixa_p bigint, ie_status_atend_p bigint, nr_prescricao_p bigint) AS $body$
DECLARE


qt_exames_baixa_w bigint;


BEGIN

select 	count(*)
into STRICT	qt_exames_baixa_w
from 	prescr_procedimento
where	dt_atualizacao < dt_parametro_p
and	(nr_seq_exame IS NOT NULL AND nr_seq_exame::text <> '')
and	ie_status_atend = coalesce(ie_status_atend_p,ie_status_atend)
and	nr_prescricao = coalesce(nr_prescricao_p, nr_prescricao)
and	cd_motivo_baixa = 0;

while qt_exames_baixa_w > 0 loop
	update 	prescr_procedimento
	set 	cd_motivo_baixa = coalesce(cd_motivo_baixa_p,1),
		dt_baixa	= dt_atualizacao,
		nm_usuario	= 'Baca04/06'
	where	dt_atualizacao < dt_parametro_p
	and	ie_status_atend = coalesce(ie_status_atend_p,ie_status_atend)
	and	(nr_seq_exame IS NOT NULL AND nr_seq_exame::text <> '')
	and	nr_prescricao = coalesce(nr_prescricao_p, nr_prescricao)
	and	cd_motivo_baixa = 0 LIMIT 999;

	commit;

	qt_exames_baixa_w := qt_exames_baixa_w - 1000;

end loop;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
