SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_bordero_pagamento_estab () AS $body$
DECLARE


nr_bordero_w			bigint;
cd_estabelecimento_w		integer;

c01 CURSOR FOR
SELECT	a.nr_bordero
from	bordero_pagamento a
where	exists (select	1
		from	titulo_pagar x
		where	x.nr_bordero		= a.nr_bordero
		and	x.cd_estabelecimento	<> a.cd_estabelecimento);


BEGIN

open c01;
loop
fetch c01 into
	nr_bordero_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	select	min(cd_estabelecimento)
	into STRICT	cd_estabelecimento_w
	from	titulo_pagar
	where	nr_bordero	= nr_bordero_w;

	update	bordero_pagamento
	set	cd_estabelecimento	= cd_estabelecimento_w
	where	nr_bordero		= nr_bordero_w;

end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
