CREATE OR REPLACE PROCEDURE baca_brasindice_preco_pk () AS $body$
DECLARE


qt_col_w	bigint;
nr_sequencia_W	bigint;


i			bigint;
CD_LABORATORIO_w	varchar(6);
CD_MEDICAMENTO_w	varchar(6);
CD_APRESENTACAO_w	varchar(6);
DT_INICIO_VIGENCIA_w	timestamp;

C01 CURSOR FOR
	SELECT	cd_apresentacao,
		cd_medicamento,
		cd_laboratorio,
		dt_inicio_vigencia
	from	brasindice_preco
	where	coalesce(nr_sequencia::text, '') = ''
	order by dt_inicio_vigencia,1,2,3;


BEGIN

select 	count(*)
into STRICT	qt_col_w
from 	user_cons_columns
where 	constraint_name = 'BRAPREC_PK'
and	column_name	= 'NR_SEQUENCIA';

if (qt_col_w	= 0) then
	begin

	i:= 0;
	open C01;
	loop
	fetch C01 into
		CD_APRESENTACAO_w,
		CD_MEDICAMENTO_w,
		CD_LABORATORIO_w,
		DT_INICIO_VIGENCIA_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		update 	BRASINDICE_PRECO
		set 	nr_sequencia = nextval('brasindice_preco_seq')
		where 	coalesce(nr_sequencia::text, '') = ''
		and 	cd_apresentacao = CD_APRESENTACAO_w
		and 	cd_medicamento = CD_MEDICAMENTO_w
		and 	cd_laboratorio = CD_LABORATORIO_w
		and 	dt_inicio_vigencia = DT_INICIO_VIGENCIA_w;

		i:= i + 1;

		if (i > 1000) then
			commit;
			i:= 0;
		end if;

		end;
	end loop;
	close C01;

	commit;

	CALL Exec_Sql_Dinamico('BRAPREC_PK',   ' alter table BRASINDICE_PRECO modify (nr_sequencia number(10,0) not null) ');
	CALL Exec_Sql_Dinamico('BRAPREC_PK',   ' alter table BRASINDICE_PRECO drop constraint BRAPREC_PK ');
	CALL Exec_Sql_Dinamico('BRAPREC_PK',   ' alter table BRASINDICE_PRECO add ( CONSTRAINT BRAPREC_PK Primary Key (nr_sequencia)) ');

	end;
end if;

end;
$body$
