SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_cd_empresa () AS $body$
DECLARE



cd_empresa_w		bigint;

c01 CURSOR FOR
SELECT	a.nr_sequencia,
	a.cd_estabelecimento
from	pat_conta_contabil a;
Vet01	C01%RowType;


BEGIN
	open C01;
	loop
	fetch C01 into
		Vet01;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select 	max(cd_empresa)
		Into STRICT 	cd_empresa_w
		from 	estabelecimento
		where 	cd_estabelecimento 	= vet01.cd_estabelecimento;

		update	pat_conta_contabil
			set	cd_empresa		= cd_empresa_w
			where	nr_sequencia		= vet01.nr_sequencia
			and	cd_estabelecimento	= vet01.cd_estabelecimento;

		commit;
		end;

	end loop;
	close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
