CREATE OR REPLACE PROCEDURE baca_cheque_cr_alt_venc () AS $body$
DECLARE


qt_registros_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_registros_w
from	CHEQUE_CR_ALT_VENC a
where 	not exists (SELECT	1
	from 	CHEQUE_CR b
	where	a.NR_SEQ_CHEQUE = b.NR_SEQ_CHEQUE)
and	(a.NR_SEQ_CHEQUE IS NOT NULL AND a.NR_SEQ_CHEQUE::text <> '');

if (qt_registros_w > 0) then

	CALL Exec_Sql_Dinamico('CHALTVE_CHEQUES_FK',	' delete from CHEQUE_CR_ALT_VENC a '||
						' where not exists ( select 1 from CHEQUE_CR b where a.NR_SEQ_CHEQUE = b.NR_SEQ_CHEQUE) '||
						' and a.NR_SEQ_CHEQUE is not null ');

	CALL Exec_Sql_Dinamico('CHALTVE_CHEQUES_FK', ' Alter Table CHEQUE_CR_ALT_VENC drop Constraint CHALTVE_CHEQUES_FK ');

	CALL Exec_Sql_Dinamico('CHALTVE_CHEQUES_FK', ' Alter Table CHEQUE_CR_ALT_VENC add (Constraint CHALTVE_CHEQUES_FK Foreign Key (NR_SEQ_CHEQUE) '||
						' References CHEQUE_CR (NR_SEQ_CHEQUE) on delete cascade) ');
end if;

end;
