SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_cirurgia_desc_seq () AS $body$
DECLARE



nr_cirurgia_w	bigint;
nr_sequencia_w	bigint;
qt_existe_w	bigint;
i		integer := 0;
ds_retorno_w	varchar(100);

C01 CURSOR FOR
SELECT	nr_cirurgia
from	cirurgia_descricao
where	coalesce(nr_sequencia::text, '') = '';


BEGIN

OPEN C01;
LOOP
FETCH C01 into	nr_cirurgia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	i := i + 1;
	select	nextval('cirurgia_descricao_seq')
	into STRICT	nr_sequencia_w
	;

	update	cirurgia_descricao
	set	nr_sequencia	= nr_sequencia_w
	where	nr_cirurgia	= nr_cirurgia_w;

	if (i > 5000) then
		i	:= 0;
		commit;
	end if;
	end;
END LOOP;
close c01;
commit;

CALL Exec_Sql_Dinamico('Edilson','alter table cirurgia_descricao modify nr_sequencia number(10) not null');

select	COUNT(*)
into STRICT	qt_existe_w
from	user_cons_columns
where	table_name = 'CIRURGIA_DESCRICAO'
and	constraint_name = 'CIRDESC_PK'
and	column_name = 'NR_SEQUENCIA';

if (qt_existe_w = 0) then
	CALL Exec_Sql_Dinamico('Edilson','alter table cirurgia_descricao drop constraint CIRDESC_PK');

	delete	from INDICE_ATRIBUTO
	where	nm_tabela = 'CIRURGIA_DESCRICAO'
	and	nm_indice = 'CIRDESC_PK';

	ds_retorno_w := Executar_SQL_Dinamico('ALTER TABLE CIRURGIA_DESCRICAO ADD
			( CONSTRAINT CIRDESC_PK   Primary Key  (NR_SEQUENCIA)) ', ds_retorno_w);

	commit;
end if;

select	count(*)
into STRICT	qt_existe_w
from	INDICE_ATRIBUTO
where	nm_tabela	= 'CIRURGIA_DESCRICAO'
and	nm_indice	= 'CIRDESC_PK';

if (qt_existe_w = 0) then
	insert	into INDICE_ATRIBUTO(
		nm_tabela,
		nm_indice,
		nr_sequencia,
		nm_atributo,
		dt_atualizacao,
		nm_usuario)
	values ('CIRURGIA_DESCRICAO',
		'CIRDESC_PK',
		1,
		'NR_SEQUENCIA',
		clock_timestamp(),
		'Tasy');
end if;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
