CREATE OR REPLACE PROCEDURE baca_classif_bordero_rec (dt_inicial_p timestamp, dt_final_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
nr_titulo_w		bigint;
nr_seq_baixa_w		bigint;
cd_estabelecimento_w	bigint;	
 
c01 CURSOR FOR 
SELECT	a.nr_titulo, 
	a.nr_sequencia, 
	c.cd_estabelecimento 
from	titulo_receber c, 
	bordero_tit_rec b, 
	titulo_receber_liq a 
where	a.nr_titulo	= b.nr_titulo 
and	a.nr_titulo	= c.nr_titulo 
and	a.nr_bordero	= b.nr_bordero 
and	coalesce(a.vl_glosa,0) < 0 
and	not exists (SELECT	1 
			from	titulo_rec_liq_cc x 
			where	x.nr_titulo	= a.nr_titulo 
			and	x.nr_seq_baixa	= a.nr_sequencia) 
and	a.dt_recebimento between dt_inicial_p and fim_dia(dt_final_p);


BEGIN 
 
open c01;
loop 
fetch c01 into 
	nr_titulo_w, 
	nr_seq_baixa_w, 
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
 
	CALL gerar_titulo_rec_liq_cc(cd_estabelecimento_w, 
				null, 
				nm_usuario_p, 
				nr_titulo_w, 
				nr_seq_baixa_w);
end loop;
close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
