CREATE OR REPLACE PROCEDURE baca_cme_conjunto () AS $body$
DECLARE


nr_seq_item_w		bigint;
nr_seq_conjunto_w	bigint;
nm_item_w		varchar(255);

/* Busca os itens cadastrados e ativos */

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.nm_item
	from	cm_item a
	where	a.ie_situacao = 'A'
	order by	a.nr_sequencia;


BEGIN

open	C01;
loop
fetch	C01 into
	nr_seq_item_w,
	nm_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	nextval('cm_conjunto_seq')
	into STRICT	nr_seq_conjunto_w
	;

	insert into cm_conjunto(
		nr_sequencia,
		cd_setor_atendimento,
		nm_conjunto,
		nr_seq_classif,
		dt_atualizacao,
		nm_usuario,
		ie_situacao,
		nr_seq_embalagem,
		cd_estabelecimento,
		qt_ponto,
		ie_agendamento)
	values (nr_seq_conjunto_w,
		103,
		nm_item_w,
		71,
		clock_timestamp(),
		'TasyCME',
		'A',
		25,
		1,
		1,
		'S');

	insert into cm_conjunto_item(
		nr_seq_conjunto,
		nr_seq_item,
		qt_item,
		dt_atualizacao,
		nm_usuario)
	values (nr_seq_conjunto_w,
		nr_seq_item_w,
		1,
		clock_timestamp(),
		'TasyCME');

	end;
end loop;
close C01;

commit;

end;
