CREATE OR REPLACE PROCEDURE baca_cnpj_raiz_pj () AS $body$
DECLARE


cont_w		bigint;


BEGIN

select	count(*)
into STRICT	cont_w
from	pessoa_juridica
where	coalesce(cd_cnpj_raiz::text, '') = '';

while	cont_w > 0 loop
	begin
	update	pessoa_juridica
	set	dt_atualizacao	= dt_atualizacao
	where	coalesce(cd_cnpj_raiz::text, '') = ''  LIMIT 5000;
	exception
	when others then
		null;
	end;
	cont_w			:= cont_w - 5000;
	commit;
end loop;

commit;

end;
