CREATE OR REPLACE PROCEDURE baca_cobranca_alt_cobrador () AS $body$
DECLARE


qt_registros_w		bigint;


BEGIN

select 	count(*)
into STRICT	qt_registros_w
from 	COBRANCA_ALT_COBRADOR a
where 	not exists (SELECT	1
	from 	COBRANCA b
	where	a.NR_SEQ_COBRANCA = b.NR_SEQUENCIA)
and 	(a.NR_SEQ_COBRANCA IS NOT NULL AND a.NR_SEQ_COBRANCA::text <> '');

if (qt_registros_w > 0) then

	CALL Exec_Sql_Dinamico('COBACOB_COBRANC_FK', ' alter trigger COBRANCA_ALT_COBRADOR_DELETE disable ');

	CALL Exec_Sql_Dinamico('COBACOB_COBRANC_FK',	' delete from COBRANCA_ALT_COBRADOR a '||
						' where	not exists (select 1 from COBRANCA b where a.NR_SEQ_COBRANCA = b.NR_SEQUENCIA) '||
						' and a.NR_SEQ_COBRANCA	is not null ');

	CALL Exec_Sql_Dinamico('COBACOB_COBRANC_FK', ' Alter Table COBRANCA_ALT_COBRADOR drop Constraint COBACOB_COBRANC_FK ');

	CALL Exec_Sql_Dinamico('COBACOB_COBRANC_FK', ' Alter Table COBRANCA_ALT_COBRADOR add (Constraint COBACOB_COBRANC_FK Foreign Key (NR_SEQ_COBRANCA) '||
						' References COBRANCA (NR_SEQUENCIA) on delete cascade) ');

	CALL Exec_Sql_Dinamico('COBACOB_COBRANC_FK', ' alter trigger COBRANCA_ALT_COBRADOR_DELETE enable ');

end if;

end;
