CREATE OR REPLACE PROCEDURE baca_conpaci_desconto (nr_interno_conta_p bigint) AS $body$
DECLARE

 
 
ie_proc_mat_w		varchar(1);
nr_sequencia_w		bigint;

 
c01 CURSOR FOR 
SELECT	'M', 
	a.nr_sequencia 
from	conta_paciente_desconto c, 
	Mat_Atend_Paciente_valor b, 
	material_atend_paciente a 
where	a.nr_interno_conta 	= nr_interno_conta_p 
and	a.nr_sequencia		= b.nr_seq_material 
and	a.nr_interno_conta	= c.nr_interno_conta 
and	coalesce(c.dt_calculo::text, '') = '' 
and	ie_tipo_valor 		= 3 

union
 
SELECT	'P', 
	a.nr_sequencia 
from 	conta_paciente_desconto c, 
	Proc_Paciente_valor b, 
	Procedimento_paciente a 
where	a.nr_interno_conta 	= nr_interno_conta_p 
and	a.nr_sequencia 		= b.nr_seq_Procedimento 
and	b.ie_tipo_valor		= 3 
and	a.nr_interno_conta	= c.nr_interno_conta 
and	coalesce(c.dt_calculo::text, '') = '' 
and	a.nr_sequencia		<> coalesce(a.nr_seq_proc_pacote,0);

 

BEGIN 
 
open c01;
loop 
fetch c01 into 
	ie_proc_mat_w, 
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
 
	if (ie_proc_mat_w = 'P') then 
		delete	from proc_paciente_valor 
		where	nr_seq_procedimento	= nr_sequencia_w 
		and	ie_tipo_valor		= 3;
	else 
		delete	from mat_atend_paciente_valor 
		where	nr_seq_material		= nr_sequencia_w 
		and	ie_tipo_valor		= 3;
	end if;
 
end loop;
close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
