DECLARE


nr_ordem_compra_w		bigint;

c01 CURSOR FOR
SELECT nr_ordem_compra
from	Ordem_Compra
where	dt_ordem_compra > clock_timestamp() - interval '180 days'
and coalesce(dt_baixa::text, '') = '';


