CREATE OR REPLACE PROCEDURE baca_convenio_estab () AS $body$
DECLARE


nr_sequencia_w		bigint;
cd_convenio_w		integer;
cd_estabelecimento_w	integer;

C01 CURSOR FOR
SELECT 	cd_convenio
from 		convenio a
where		not exists (select 1 from convenio_estabelecimento b
	where a.cd_convenio = b.cd_convenio);

BEGIN

select min(cd_estabelecimento)
into STRICT cd_estabelecimento_w
from estabelecimento;

OPEN C01;
LOOP
FETCH C01 into	cd_convenio_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select nextval('convenio_estabelecimento_seq')
	into STRICT nr_sequencia_w
	;
	insert into convenio_estabelecimento(
		nr_sequencia, dt_atualizacao, nm_usuario,
		cd_convenio, cd_estabelecimento, cd_conta_contabil,
		nr_seq_regra_funcao, nr_seq_conta)
	values (
		nr_sequencia_w,
		clock_timestamp(),
		'Tasy',
		cd_convenio_w,
		cd_estabelecimento_w,
		null, null, null);
	end;
END LOOP;
close c01;
commit;
END;
