SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_convenio_hist_padrao () AS $body$
DECLARE


cd_convenio_w			bigint;
cd_estabelecimento_w		smallint;
cd_estab_base_w			smallint;
cd_historico_w			bigint;
qt_registro_w			bigint;

c01 CURSOR FOR
SELECT	cd_estabelecimento
from	estabelecimento
where	ie_situacao = 'A'
and	qt_registro_w > 1
and	cd_estabelecimento <> cd_estab_base_w;

c02 CURSOR FOR
SELECT	cd_convenio,
	cd_historico
from	convenio_hist_padrao
order by 1;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	estabelecimento
where	ie_situacao = 'A';

select	min(cd_estabelecimento)
into STRICT	cd_estab_base_w
from	estabelecimento
where	ie_situacao = 'A';

if (qt_registro_w = 1) then
	update	convenio_hist_padrao
	set	cd_estabelecimento = cd_estab_base_w
	where	coalesce(cd_estabelecimento::text, '') = '';
end if;

open C01;
loop
fetch C01 into
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	open C02;
	loop
	fetch C02 into
		cd_convenio_w,
		cd_historico_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		select	count(*)
		into STRICT	qt_registro_w
		from	convenio_hist_padrao
		where	cd_convenio		= cd_convenio_w
		and	cd_historico		= cd_historico_w
		and	cd_estabelecimento	= cd_estabelecimento_w;

		if (qt_registro_w = 0) then
			insert into convenio_hist_padrao(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				cd_convenio,
				cd_historico,
				cd_estabelecimento)
			values ( nextval('convenio_hist_padrao_seq'),
				clock_timestamp(),
				'TasyEstab',
				clock_timestamp(),
				'TasyEstab',
				cd_convenio_w,
				cd_historico_w,
				cd_estabelecimento_w);
		end if;
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;
select	count(*)
into STRICT	qt_registro_w
from	user_tab_columns
where	table_name	= 'CONVENIO_HIST_PADRAO'
and	column_name	= 'CD_ESTABELECIMENTO'
and	nullable	= 'Y';

if (qt_registro_w > 0) then
	CALL exec_sql_dinamico('BacaConvHistPad',' alter table convenio_hist_padrao modify cd_estabelecimento not null');
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
