SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_conversao () AS $body$
DECLARE

CD_INTERNO_W			varchar(20);
CD_EXTERNO_W			varchar(20);
NR_SEQUENCIA_W			bigint;
CD_CONVENIO_W			integer		:= 32;
NM_ATRIBUTO_W			varchar(50);
DT_ATUALIZACAO_W			timestamp			:= clock_timestamp();
NM_USUARIO_W			varchar(15)	:= 'Wander';
nr_seq_mat_w			integer	:= 80078000;
nr_seq_med_w			integer   	:= 80072000;
cd_grupo_mat_w			integer	:= 0;
cd_material_w			integer;

C001 CURSOR FOR
SELECT	a.cd_material,
		b.cd_grupo_material
from		estrutura_material_v b,
		Material a
where		a.ie_situacao = 'A'
  and		a.cd_material	= b.cd_material
  and		b.cd_grupo_material in (1,2,4);


BEGIN
open 	c001;
loop
fetch c001 	into
		cd_material_w,
		cd_grupo_mat_w;
	EXIT WHEN NOT FOUND; /* apply on c001 */
		BEGIN
		select nextval('conversao_mat_convenio_seq')
		into STRICT  nr_sequencia_w
		;
		cd_interno_w		:= cd_material_w;
		if (cd_grupo_mat_w = 1) then
			begin
			nr_seq_med_w	:= nr_seq_med_w + 1;
			cd_externo_w	:= nr_seq_med_w;
			end;
		else
			begin
			nr_seq_mat_w	:= nr_seq_mat_w + 1;
			cd_externo_w	:= nr_seq_mat_w;
			end;
		end if;
		insert into conversao_material_convenio(
				NR_SEQUENCIA,
				CD_CONVENIO,
				CD_MATERIAL_CONVENIO,
				DT_ATUALIZACAO,
				NM_USUARIO,
				CD_GRUPO_MATERIAL,
				CD_SUBGRUPO_MATERIAL,
				CD_CLASSE_MATERIAL,
				CD_MATERIAL)
			values (
				NR_SEQUENCIA_W,
				CD_CONVENIO_W,
				CD_EXTERNO_W,
				DT_ATUALIZACAO_W,
				NM_USUARIO_W,
				NULL,
				NULL,
				NULL,
				CD_INTERNO_W);
		END;
end loop;
close c001;
commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
