CREATE OR REPLACE PROCEDURE baca_copiar_classif_ecd () AS $body$
DECLARE


c01 CURSOR FOR
SELECT	a.cd_conta_contabil,
	a.cd_classif_ecd
from	conta_contabil a
where	(a.cd_classif_ecd IS NOT NULL AND a.cd_classif_ecd::text <> '')
and	not exists (	select	1
			from	conta_contabil_classif_ecd y
			where	y.cd_conta_contabil	= a.cd_conta_contabil
			and	y.cd_classif_ecd	= a.cd_classif_ecd);

vet01	C01%RowType;


BEGIN

open C01;
loop
fetch C01 into
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	insert into conta_contabil_classif_ecd(
		nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		cd_conta_contabil,
		cd_classif_ecd,
		cd_centro_custo,
		ie_situacao)
	values (	nextval('conta_contabil_classif_ecd_seq'),
		'CopiaECD',
		clock_timestamp(),
		'CopiaECD',
		clock_timestamp(),
		vet01.cd_conta_contabil,
		vet01.cd_classif_ecd,
		null,
		'A');

	end;
end loop;
close C01;

commit;

end;
