CREATE OR REPLACE PROCEDURE baca_cotacao_3 () AS $body$
DECLARE


ds_retorno_w			varchar(100);
cd_cgc_w			varchar(14);
nr_seq_cot_item_forn_w	bigint;
nr_cot_compra_w		bigint;
nr_item_cot_compra_w		integer;


/*
1 - Atualiza o campo nr_seq_cot_item_forn da tabela COT_COMPRA_ITEM
*/
C01 CURSOR FOR
SELECT	cd_cgc_fornecedor_venc_alt,
	nr_cot_compra,
	nr_item_cot_compra
from	cot_compra_item
where	(cd_cgc_fornecedor_venc_alt IS NOT NULL AND cd_cgc_fornecedor_venc_alt::text <> '');


BEGIN

OPEN C01;
LOOP
	FETCH C01 into
		cd_cgc_w,
		nr_cot_compra_w,
		nr_item_cot_compra_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	nr_sequencia
	into STRICT	nr_seq_cot_item_forn_w
	from	cot_compra_forn_item
	where	nr_cot_compra = nr_cot_compra_w
	and	nr_item_cot_compra = nr_item_cot_compra_w
	and	cd_cgc_fornecedor = cd_cgc_w;

	update	cot_compra_item
	set	nr_seq_cot_item_forn = nr_seq_cot_item_forn_w
	where	nr_cot_compra = nr_cot_compra_w
	and	nr_item_cot_compra = nr_item_cot_compra_w;

	end;
end loop;
close c01;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
