SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_cot_compra_forn_item () AS $body$
DECLARE


/*Ajustar o ie_situacao da tabela cot_compra_forn_item, pois haviam registros com a situação S ou N */

qt_existe_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	cot_compra_forn_item
where	ie_situacao = 'S';

if (qt_existe_w > 0) then
	update	cot_compra_forn_item
	set	ie_situacao = 'A'
	where	ie_situacao = 'S';
end if;

select count(*)
into STRICT	qt_existe_w
from	cot_compra_forn_item
where	ie_situacao = 'N';

if (qt_existe_w > 0) then
	update	cot_compra_forn_item
	set	ie_situacao = 'I'
	where	ie_situacao = 'N';
end if;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
