BEGIN


select 	count(*)
into STRICT	qt_inconsistente_w
from 	PROCEDIMENTO_AUTORIZADO a
where 	not exists ( 	SELECT 1
			from AGEINT_EXAME_LAB b
			where 1 = 1
			and a.NR_SEQ_AGEINT_EXAME_LAB = b.NR_SEQUENCIA)
and 	(a.NR_SEQ_AGEINT_EXAME_LAB IS NOT NULL AND a.NR_SEQ_AGEINT_EXAME_LAB::text <> '');


if (coalesce(qt_inconsistente_w,0) > 0) then


	update	procedimento_autorizado a
	set	a.nr_seq_ageint_exame_lab  = NULL
	where	a.nr_seq_ageint_exame_lab is  not null
	and	not exists (SELECT	1
			   from		ageint_exame_lab x
			   where	x.nr_sequencia = a.nr_seq_ageint_exame_lab);

	commit;
end if;


select 	count(*)
into STRICT	qt_index_w
from	user_indexes
where	index_name = 'PROAUTO_AGIEXLB_FK_I';

if (qt_index_w = 0) then
	-- Criar Indice da tabela: PROCEDIMENTO_AUTORIZADO
	CALL Exec_sql_Dinamico('ralves', ' Create Index PROAUTO_AGIEXLB_FK_I on PROCEDIMENTO_AUTORIZADO(NR_SEQ_AGEINT_EXAME_LAB) ');

end if;


select	count(*)
into STRICT	qt_constraint_w
from	user_constraints
where	constraint_name = 'PROAUTO_AGIEXLB_FK';

if (qt_constraint_w = 0) then
	-- Criar Integridade ReferÃªncial da tabela: PROCEDIMENTO_AUTORIZADO
	CALL Exec_sql_Dinamico('ralves', ' Alter Table PROCEDIMENTO_AUTORIZADO add ( Constraint PROAUTO_AGIEXLB_FK Foreign Key ( NR_SEQ_AGEINT_EXAME_LAB) References AGEINT_EXAME_LAB(NR_SEQUENCIA)) ');

end if;



commit;

end;
