SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_ctb_criterio_rateio_estab () AS $body$
DECLARE



cd_estab_min_w		smallint;
cd_estabelecimento_w		smallint;
nr_sequencia_w		bigint;
qt_registro_w			bigint	:= 0;
ds_criterio_w			varchar(45);
dt_atualizacao_w		timestamp;
nm_usuario_w			varchar(15);
dt_atualizacao_nrec_w	timestamp;
nm_usuario_nrec_w		varchar(15);


c01 CURSOR FOR
	SELECT	cd_estabelecimento
	from	estabelecimento
	where	cd_estabelecimento not in (cd_estab_min_w);

c02 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.ds_criterio,
		a.dt_atualizacao,
		a.nm_usuario
	from	ctb_criterio_rateio a
	where	cd_estabelecimento = cd_estab_min_w;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	ctb_criterio_rateio
where	(cd_estabelecimento IS NOT NULL AND cd_estabelecimento::text <> '');

if (qt_registro_w = 0) then

	select	min(cd_estabelecimento)
	into STRICT	cd_estab_min_w
	from	estabelecimento;

	update	ctb_criterio_rateio
	set	cd_estabelecimento = cd_estab_min_w,
		dt_atualizacao = clock_timestamp();

	open c01;
	loop
	fetch c01 into
		cd_estabelecimento_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		open c02;
		loop
		fetch c02 into
			nr_sequencia_w,
			ds_criterio_w,
			dt_atualizacao_w,
			nm_usuario_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */

		insert into ctb_criterio_rateio(
			nr_sequencia,
			ds_criterio,
			dt_atualizacao,
			nm_usuario,
			cd_estabelecimento)
		values (nextval('ctb_criterio_rateio_seq'),
			ds_criterio_w,
			clock_timestamp(),
			'Baca_Criterio',
			cd_estabelecimento_w);
		end loop;
		close c02;
		end;

	end loop;
	close c01;

end if;
commit;
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
