CREATE TYPE campos AS (nr_interno_conta bigint);


CREATE OR REPLACE PROCEDURE baca_custo_conta ( dt_parametro_P timestamp, nr_seq_protocolo_p bigint) AS $body$
DECLARE

type Vetor is table of campos index by integer;

dt_parametro_inicio_w       	timestamp;
dt_parametro_fim_w        	timestamp;
NR_INTERNO_CONTA_W         	bigint;
i					integer;
k					integer;
Vetor_Proc_w				Vetor;

 
C01 CURSOR FOR 
SELECT a.nr_interno_conta 
from 	conta_paciente a 
where a.dt_mesano_referencia between dt_parametro_inicio_w and dt_parametro_fim_w 
 and a.ie_status_acerto 		= 2 
 and nr_seq_protocolo_p		= 0 

union
 
SELECT a.nr_interno_conta 
from 	conta_paciente a 
where a.ie_status_acerto 		= 2 
 and a.nr_seq_protocolo	 	= nr_seq_protocolo_p 
 and nr_seq_protocolo_p		<> 0;


BEGIN 
 
dt_parametro_fim_w         := last_day(Trunc(dt_parametro_p,'dd')) + 86399/86400;
dt_parametro_Inicio_w		 	:= trunc(dt_parametro_p,'month');
 
i	:= 1;
OPEN C01;
LOOP 
FETCH C01 into	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	Vetor_Proc_w[i].nr_interno_conta := nr_interno_conta_w;
	i := i + 1;
	end;
END LOOP;
close c01;
 
i := Vetor_Proc_w.count;
commit;
 
for k in 1.. i loop 
	begin 
	nr_interno_conta_w	:= Vetor_Proc_w[k].nr_interno_conta;
	/*if	(trunc(k /100,0) = (k / 100)) then 
		insert into logxxxx_tasy values(sysdate, 'Tasy', 999, k || '/' || i); 
	end if;*/
 
	atualizar_custo_conta(nr_interno_conta_w);
	commit;
	end;
end loop;
/*insert into logxxxx_tasy values(sysdate, 'Tasy', 999, k || '/' || i);*/
 
commit;
END;
$body$
