CREATE OR REPLACE PROCEDURE baca_custo_procedimento_pk () AS $body$
DECLARE


/* OS 78272
Baca criado por motivo de alteração da PK da CUSTO_PROCEDIMENTO de
CD_ESTABELECIMENTO, CD_TABELA_CUSTO, CD_PROCEDIMENTO e IE_ORIGEM_PROCED para NR_SEQUENCIA - com sequence */
cd_estabelecimento_w	smallint;
cd_procedimento_w	bigint;
cd_tabela_custo_w	integer;
ie_origem_proced_w	bigint;
nr_sequencia_w	bigint;

c01 CURSOR FOR
SELECT	cd_estabelecimento,
	cd_tabela_custo,
	cd_procedimento,
	ie_origem_proced
from	custo_procedimento
where	coalesce(nr_sequencia::text, '') = ''
order by 1,2,3,4;


BEGIN

open c01;
loop
fetch c01 into
	cd_estabelecimento_w,
	cd_tabela_custo_w,
	cd_procedimento_w,
	ie_origem_proced_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	select nextval('custo_procedimento_seq')
	into STRICT	nr_sequencia_w
	;

	update	custo_procedimento
	set	nr_sequencia		= nr_sequencia_w
	where	cd_estabelecimento	= cd_estabelecimento_w
	and	cd_tabela_custo	= cd_tabela_custo_w
	and	cd_procedimento	= cd_procedimento_w
	and	ie_origem_proced	= ie_origem_proced_w;

end loop;
close c01;

commit;

/* Exclui pk antiga*/

CALL exec_sql_dinamico('CustoProced', 'alter table custo_procedimento drop constraint cusproc_pk');
CALL exec_sql_dinamico('CustoProced', 'drop index cusproc_pk');

/* Retira obrigatoriedade dos campos Proced e origem proced*/

CALL exec_sql_dinamico('CustoProced', 'alter table custo_procedimento modify cd_procedimento null');
CALL exec_sql_dinamico('CustoProced', 'alter table custo_procedimento modify ie_origem_proced null');

/* Cria nova chave primaria */

CALL exec_sql_dinamico('CustoProced', 'alter table custo_procedimento add (constraint CUSPROC_PK Primary Key (nr_sequencia) USING INDEX Tablespace TASY_INDEX)');

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
