CREATE OR REPLACE PROCEDURE baca_cus_criterio_distr_orc () AS $body$
DECLARE


cd_sequencia_criterio_w	bigint;
qt_registro_w		bigint;

c01 CURSOR FOR
SELECT	cd_sequencia_criterio
from	criterio_distr_orc
where	ie_criterio_centro	= 'S'
and	coalesce(ie_tipo_centro_controle,'X') = 'X';


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	criterio_distr_orc
where	ie_criterio_centro		= 'S'
and	coalesce(ie_tipo_centro_controle,'X') = 'X';

if (qt_registro_w > 0) then
	begin
	open C01;
	loop
	fetch C01 into
		cd_sequencia_criterio_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		update	criterio_distr_orc
		set	ie_tipo_centro_controle	= '2'
		where	cd_sequencia_criterio	= cd_sequencia_criterio_w;

		/*insert into logxxxx_tasy(
			cd_log,
			ds_log,
			nm_usuario,
			dt_atualizacao)
		values(	9822,
			'Criterio=' || cd_sequencia_criterio_w,
			'Tasy',
			sysdate);*/
		end;
	end loop;
	close C01;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
