SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_data_result_pro () AS $body$
DECLARE

 
dt_prescricao_w		timestamp;
nr_prescricao_w		bigint;
nr_sequencia_w		integer;
dt_prev_execucao_w	timestamp;
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
cd_setor_atendimento_w	integer;
dt_entrega_w		timestamp;
nr_seq_proc_interno_w	bigint;

/*Ajusta a data de resultado da prescr_procedimento com base na data prevista de execução */
 
 
C01 CURSOR FOR 
	SELECT	a.dt_prescricao, 
		a.nr_prescricao, 
		b.nr_sequencia, 
		b.dt_prev_execucao, 
		b.cd_procedimento, 
		b.ie_origem_proced, 
		b.cd_setor_atendimento, 
		b.nr_seq_proc_interno 
	from	prescr_procedimento b, 
		prescr_medica a 
	where	a.nr_prescricao	= b.nr_prescricao 
	and	coalesce(b.nr_seq_exame::text, '') = '' 
	and	(b.cd_setor_atendimento IS NOT NULL AND b.cd_setor_atendimento::text <> '') 
	and	(b.dt_prev_execucao IS NOT NULL AND b.dt_prev_execucao::text <> '');

BEGIN
 
open C01;
loop 
fetch C01 into 
	dt_prescricao_w, 
	nr_prescricao_w, 
	nr_sequencia_w, 
	dt_prev_execucao_w, 
	cd_procedimento_w, 
	ie_origem_proced_w, 
	cd_setor_atendimento_w, 
	nr_seq_proc_interno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	 
	dt_entrega_w	:= null;
 
	begin 
	dt_entrega_w := OBTER_ENTREGA_LAUDO(nr_prescricao_w, dt_prev_execucao_w, nr_seq_proc_interno_w, cd_procedimento_w, ie_origem_proced_w, cd_setor_atendimento_w, 'Tasy', dt_entrega_w);
	if (dt_entrega_w IS NOT NULL AND dt_entrega_w::text <> '') then 
		update	prescr_procedimento 
		set	dt_resultado	= dt_entrega_w 
		where	nr_prescricao	= nr_prescricao_w 
		and	nr_sequencia	= nr_sequencia_w;
 
		commit;
 
	end if;
	exception 
	when others then 
		dt_entrega_w	:= null;
	end;
	 
end loop;
close C01;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baca_data_result_pro () FROM PUBLIC;

