SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_declaracao_proposta () AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
cd_pessoa_fisica_w		varchar(10);
nr_seq_proposta_w		bigint;
nr_seq_pessoa_proposta_w	bigint;
nr_seq_segurado_w		bigint;
qt_registro_w			bigint	:= 0;

C01 CURSOR FOR
	SELECT	a.cd_beneficiario,
		a.nr_seq_proposta,
		a.nr_sequencia
	from	pls_proposta_beneficiario	a
	where	exists (SELECT	1
			from	pls_segurado	x
			where	a.nr_sequencia	= x.nr_seq_pessoa_proposta)
	and	exists (select	1
			from	pls_declaracao_segurado	x
			where	a.cd_beneficiario	= x.cd_pessoa_fisica
			and	a.nr_seq_proposta	= x.nr_seq_proposta_adesao
			and	coalesce(x.nr_seq_segurado::text, '') = '');


BEGIN
open C01;
loop
fetch C01 into
	cd_pessoa_fisica_w,
	nr_seq_proposta_w,
	nr_seq_pessoa_proposta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_segurado_w
	from	pls_segurado	a
	where	a.nr_seq_pessoa_proposta	= nr_seq_pessoa_proposta_w;

	if (nr_seq_segurado_w IS NOT NULL AND nr_seq_segurado_w::text <> '') then
		update	pls_declaracao_segurado
		set	nr_seq_segurado		= nr_seq_segurado_w
		where	cd_pessoa_fisica	= cd_pessoa_fisica_w
		and	nr_seq_proposta_adesao	= nr_seq_proposta_w;

		qt_registro_w	:= qt_registro_w + 1;

		if (qt_registro_w = 300) then
			commit;

			qt_registro_w	:= 0;
		end if;
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baca_declaracao_proposta () FROM PUBLIC;

