CREATE OR REPLACE PROCEDURE baca_define_conta_aih () AS $body$
DECLARE

 
nr_atendimento_w	bigint;
nr_aih_w		bigint;
nr_interno_conta_w	bigint;
qt_contas_w		bigint;

ie_tipo_laudo_sus_w	smallint;
nr_laudo_sus_w		bigint;
nr_sequencia_w		bigint;
i			bigint		:= 0;

 
 
c01 CURSOR FOR 
SELECT	nr_atendimento, 
	nr_aih 
from	sus_aih 
where	coalesce(nr_interno_conta::text, '') = '';

 

BEGIN 
 
 
OPEN C01;
LOOP 
FETCH C01 into	nr_atendimento_w, 
		nr_aih_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
 
	select	count(*) 
	into STRICT	qt_contas_w 
	from	convenio b, 
		conta_paciente a 
	where	a.nr_atendimento	= nr_atendimento_w 
	and	a.cd_convenio_parametro	= b.cd_convenio 
	and	b.ie_tipo_convenio	= 3;
 
	if (qt_contas_w = 1) then 
		begin 
 
		select	coalesce(max(a.nr_interno_conta),0) 
		into STRICT	nr_interno_conta_w 
		from	convenio b, 
			conta_paciente a 
		where	a.nr_atendimento	= nr_atendimento_w 
		and	a.cd_convenio_parametro	= b.cd_convenio 
		and	b.ie_tipo_convenio	= 3;
 
		if (nr_interno_conta_w	> 0) then 
			begin 
			begin 
			update sus_aih 
			set	nr_interno_conta = nr_interno_conta_w 
			where	nr_atendimento	 = nr_atendimento_w;
			exception 
  				when others then 
				 nr_atendimento_w := nr_atendimento_w;
			end;
 
			begin 
			update sus_laudo_paciente 
			set	nr_interno_conta = nr_interno_conta_w 
			where	nr_atendimento	 = nr_atendimento_w;
			exception 
  				when others then 
				 nr_atendimento_w := nr_atendimento_w;
			end;
			end;
		end if;
		end;
	end if;
	commit;
	end;
END LOOP;
close c01;
 
 
END;
