CREATE OR REPLACE PROCEDURE baca_define_conta_movto () AS $body$
DECLARE



nr_movimento_estoque_w		bigint;
cd_estabelecimento_w			smallint;
cd_material_w				integer;
cd_setor_atendimento_w		integer;
cd_local_estoque_w			smallint;
dt_movimento_estoque_w		timestamp;
cd_centro_custo_w			integer;
cd_conta_contabil_w			varchar(20);

c01 CURSOR FOR
SELECT	nr_movimento_estoque,
	cd_estabelecimento,
	cd_material,
	cd_setor_atendimento,
	cd_local_estoque,
	dt_movimento_estoque
from	movimento_estoque
where	(cd_centro_custo IS NOT NULL AND cd_centro_custo::text <> '')
and	coalesce(cd_conta_contabil::text, '') = '';


BEGIN

