EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	SELECT * FROM Define_Conta_Material(cd_estabelecimento_w, cd_material_w, 2, null, cd_setor_atendimento_w, null, null, null, null, null, cd_local_estoque_w, null, dt_movimento_estoque_w, cd_conta_contabil_w, cd_centro_custo_w, null) INTO STRICT cd_conta_contabil_w, cd_centro_custo_w;

	update	movimento_estoque
	set	cd_conta_contabil = cd_conta_contabil_w
