SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_deletar_pf () AS $body$
DECLARE


/*Deleta todas as pessoas fisicas que não possuem vínculos com outras tabelas e não são usuários*/

cd_pessoa_fisica_w 	varchar(10);
qt_controle_w		bigint;

C01 CURSOR FOR
SELECT 	cd_pessoa_fisica
from	pessoa_fisica a
where	 not exists (select 1 from usuario b
		where a.cd_pessoa_fisica = b.cd_pessoa_fisica);




BEGIN

qt_controle_w :=0;

open C01;
loop
fetch C01 into
	cd_pessoa_fisica_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		delete	FROM compl_pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_w;

		delete from pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_w;
		qt_controle_w := qt_controle_w + 1;

		if (qt_controle_w > 1000) then
			qt_controle_w := 0;
			commit;
		end if;

	exception
	when others then
		qt_controle_w := qt_controle_w + 1;
	end;
end loop;
close C01;
commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE baca_deletar_pf () FROM PUBLIC;

