CREATE OR REPLACE PROCEDURE baca_ds_material_sem_acento () AS $body$
DECLARE



ds_material_sem_acento_w		varchar(255);
cd_material_w			integer;

c01 CURSOR FOR
SELECT	/*+ INDEX (A MATERIA_I9) */	cd_material,
	upper(substr(elimina_acentuacao(ds_material),1,255))
from	material
where	upper(substr(elimina_acentuacao(ds_material),1,255)) <> upper(ds_material_sem_acento);


BEGIN

open c01;
loop
	fetch c01 into
		cd_material_w,
		ds_material_sem_acento_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

	update	material
	set	ds_material_sem_acento = ds_material_sem_acento_w
	where	cd_material = cd_material_w;

end loop;
close c01;

END;
$body$
