SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_ds_servico_ptu_a500 () AS $body$
DECLARE

 
tb_nr_sequencia_w	pls_util_cta_pck.t_number_table;
tb_ds_servico_new_w	pls_util_cta_pck.t_varchar2_table_255;
dt_referencia_w		timestamp := to_date('01/01/2016');

C01 CURSOR FOR 
	SELECT	s.nr_sequencia, 
		upper(trim(both ptu_somente_caracter_permitido(s.ds_servico,'AN'))) ds_servico_new 
	from	ptu_nota_servico	s, 
		ptu_nota_cobranca	c, 
		ptu_fatura		p 
	where	p.nr_sequencia	= c.nr_seq_fatura 
	and	c.nr_sequencia	= s.nr_seq_nota_cobr 
	and	(p.nr_seq_pls_fatura IS NOT NULL AND p.nr_seq_pls_fatura::text <> '') 
	and	coalesce(s.ds_servico_aux::text, '') = '' 
	and	p.dt_atualizacao_nrec >= dt_referencia_w;


BEGIN 
 
open C01;
loop 
	fetch C01 bulk collect into tb_nr_sequencia_w, tb_ds_servico_new_w 
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_sequencia_w.count = 0;
 
	forall	i in tb_nr_sequencia_w.first..tb_nr_sequencia_w.last 
		update	ptu_nota_servico 
		set	ds_servico_aux	= tb_ds_servico_new_w(i) 
		where 	nr_sequencia	= tb_nr_sequencia_w(i);
	commit;
end loop;
close C01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
