CREATE OR REPLACE PROCEDURE baca_dt_competencia_copartic () AS $body$
DECLARE

 
tb_nr_seq_conta_w	pls_util_cta_pck.t_number_table;
tb_dt_mes_competencia_w	pls_util_cta_pck.t_date_table;

C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		dt_mes_competencia 
	from	pls_conta_v	a 
	where	exists (SELECT	1 
			from	pls_conta_copartic_contab b 
			where	b.nr_seq_conta	= a.nr_sequencia);


BEGIN 
-- este baca foi criado para alimentar os campos de data referencia, isto por questões de performance 
-- para que não seja necessário utilizar um or dt is null nas rotinas que utilizam esta tabela 
-- o campo inicial ref é alimentado com o valor informado no campo inicial ou se este for nulo é alimentado 
-- com a data zero do oracle 31/12/1899, já o campo fim ref é alimentado com o campo fim ou se o mesmo for nulo 
-- é alimentado com a data 31/12/3000 desta forma podemos utilizar um between ou fazer uma comparação com estes campos 
-- sem precisar se preocupar se o campo vai estar nulo 
open C01;
loop 
	fetch C01 bulk collect into tb_nr_seq_conta_w, tb_dt_mes_competencia_w 
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_seq_conta_w.count = 0;
 
	forall	i in tb_nr_seq_conta_w.first..tb_nr_seq_conta_w.last 
		update	pls_conta_copartic_contab 
		set	dt_mes_competencia	= tb_dt_mes_competencia_w(i) 
		where 	nr_seq_conta 		= tb_nr_seq_conta_w(i);
	commit;
end loop;
close C01;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
