CREATE OR REPLACE PROCEDURE baca_dt_vig_regra_carenc_plano () AS $body$
DECLARE

 
tb_nr_sequencia_w	pls_util_cta_pck.t_number_table;
tb_dt_inicio_vig_ref_w	pls_util_cta_pck.t_date_table;
tb_dt_fim_vig_ref_w	pls_util_cta_pck.t_date_table;

C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		trunc(pls_util_pck.obter_dt_vigencia_null(dt_inicio_vig_plano, 'I'),'dd') dt_inicio_vigencia_ref, 
		fim_dia(pls_util_pck.obter_dt_vigencia_null(dt_fim_vig_plano, 'F')) dt_fim_vigencia_ref 
	from	pls_carencia 
	where	(nr_seq_plano IS NOT NULL AND nr_seq_plano::text <> '');


BEGIN 
open C01;
loop 
	fetch C01 bulk collect into tb_nr_sequencia_w, tb_dt_inicio_vig_ref_w, tb_dt_fim_vig_ref_w 
	limit pls_util_pck.qt_registro_transacao_w;
	exit when tb_nr_sequencia_w.count = 0;
 
	forall	i in tb_nr_sequencia_w.first..tb_nr_sequencia_w.last 
		update	pls_carencia 
		set	dt_inicio_vig_plano_ref = tb_dt_inicio_vig_ref_w(i), 
			dt_fim_vig_plano_ref = tb_dt_fim_vig_ref_w(i) 
		where 	nr_sequencia = tb_nr_sequencia_w(i);
	commit;
end loop;
close C01;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
