CREATE OR REPLACE PROCEDURE baca_eis_censo_diario ( dt_mes_referencia_p timestamp, nm_usuario_p text) AS $body$
DECLARE

 
dt_inicial_w	timestamp;
dt_final_w		timestamp;
dt_referencia_w	timestamp;


BEGIN 
dt_inicial_w		:= trunc(dt_mes_referencia_p,'month');
dt_final_w		:= last_day(dt_inicial_w) + 86399/86400;
 
dt_referencia_w	:= dt_inicial_w;
while dt_referencia_w < dt_final_w LOOP 
	begin 
	CALL gerar_eis_ocupacao_hospitalar(dt_referencia_w, nm_usuario_p);
	dt_referencia_w	:= dt_referencia_w + 1;
	end;
end loop;
 
end;
