CREATE OR REPLACE PROCEDURE baca_eis_resultado ( dt_parametro_p timestamp) AS $body$
DECLARE

 
			 
C01 CURSOR FOR 
SELECT	trunc(b.dt_mesano_referencia,'Month') 
from	convenio c, conta_paciente b, conta_paciente_resumo a 
where	a.nr_interno_conta	= b.nr_interno_conta 
and 	b.cd_convenio_parametro	= c.cd_convenio 
and	a.dt_referencia between trunc(dt_parametro_p,'month') and 
		trunc(dt_parametro_p,'dd') +86399/86400 
group by trunc(b.dt_mesano_referencia,'Month');
			
dt_mesano_referencia_w	timestamp;		

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	dt_mesano_referencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	CALL gerar_eis_resultado(dt_mesano_referencia_w,'Tasy');
	end;
end loop;
close C01;
commit;
 
end;
