SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_empr_param_conta_contab () AS $body$
DECLARE


qt_reg_w			smallint;
nr_sequencia_w		bigint;
cd_empresa_w		smallint;
cd_estabelecimento_w	smallint;

c01 CURSOR FOR
SELECT	cd_sequencia_parametro,
	cd_estabelecimento
from	parametros_conta_contabil
where	(cd_estabelecimento IS NOT NULL AND cd_estabelecimento::text <> '');


BEGIN
begin
select	count(*)
into STRICT	qt_reg_w
from	user_triggers
where	trigger_name = 'PARAM_CONTA_CONTABIL_ATUAL';
exception
when others then
	qt_reg_w	:= 0;
end;

if (qt_reg_w > 0) then
	CALL exec_sql_dinamico('Tasy','Alter trigger PARAM_CONTA_CONTABIL_ATUAL disable');
end if;

begin
select	count(*)
into STRICT	qt_reg_w
from	user_tables
where	table_name = 'PARAM_CONTA_CONTABIL_BKP';
exception
when others then
	qt_reg_w	:= 0;
end;

if (qt_reg_w = 0) then
	CALL exec_sql_dinamico('Tasy',' create table param_conta_contabil_bkp as select * from parametros_conta_contabil ');
end if;

open c01;
loop
fetch c01 into
	nr_sequencia_w,
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	select	cd_empresa
	into STRICT	cd_empresa_w
	from	estabelecimento
	where	cd_estabelecimento = cd_estabelecimento_w;

	begin
	update	parametros_conta_contabil
	set	cd_empresa = cd_empresa_w
	where	cd_sequencia_parametro = nr_sequencia_w;
	exception
	when others then
		null;
	end;
	end;
end loop;
close c01;

begin
select	count(*)
into STRICT	qt_reg_w
from	user_tab_columns
where	table_name = 'PARAMETROS_CONTA_CONTABIL'
and	column_name = 'CD_ESTABELECIMENTO'
and	nullable = 'Y';
exception
when others then
	qt_reg_w	:= 0;
end;

if (qt_reg_w = 0) then
	CALL exec_sql_dinamico('Tasy','ALTER TABLE parametros_conta_contabil MODIFY cd_estabelecimento null');
end if;

begin
select	count(*)
into STRICT	qt_reg_w
from	user_tab_columns
where	table_name = 'PARAMETROS_CONTA_CONTABIL'
and	column_name = 'CD_EMPRESA'
and	nullable = 'N';
exception
when others then
	qt_reg_w	:= 0;
end;

if (qt_reg_w = 0) then
	CALL exec_sql_dinamico('Tasy','ALTER TABLE parametros_conta_contabil MODIFY cd_empresa not null');
end if;

begin
select	count(*)
into STRICT	qt_reg_w
from	user_triggers
where	trigger_name = 'PARAM_CONTA_CONTABIL_ATUAL';
exception
when others then
	qt_reg_w	:= 0;
end;

if (qt_reg_w > 0) then
	CALL exec_sql_dinamico('Tasy','Alter trigger PARAM_CONTA_CONTABIL_ATUAL enable');
end if;

end;
$body$
