CREATE OR REPLACE PROCEDURE baca_etapa_cobranca () AS $body$
DECLARE


cont_w	double precision;


BEGIN

select	count(*)
into STRICT	cont_w
from	cobranca
where	coalesce(nr_seq_etapa, -1)	<> coalesce(OBTER_ETAPA_COBRANCA(NR_SEQUENCIA), 0);

while	cont_w > 0 loop

	update	cobranca
	set	nr_seq_etapa		= OBTER_ETAPA_COBRANCA(NR_SEQUENCIA)
	where	coalesce(nr_seq_etapa, -1)	<> coalesce(OBTER_ETAPA_COBRANCA(NR_SEQUENCIA), 0)  LIMIT 5000;

	cont_w	:= cont_w - 5000;

	commit;
end loop;

update	cobranca
set	nr_seq_etapa		= OBTER_ETAPA_COBRANCA(NR_SEQUENCIA)
where	coalesce(nr_seq_etapa, -1)	<> coalesce(OBTER_ETAPA_COBRANCA(NR_SEQUENCIA), 0);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
