CREATE OR REPLACE PROCEDURE baca_excluir_itens_pend_lib (cd_estabelecimento_p bigint) AS $body$
DECLARE


ie_lib_anamnese_w		varchar(10);
ie_liberar_ganho_perda_w	varchar(10);
ie_lib_sinal_vital_w		varchar(10);
ie_lib_loco_regional_w		varchar(10);
ie_lib_parecer_medico_w		varchar(10);


BEGIN

select	max(ie_lib_anamnese),
	max(ie_liberar_ganho_perda),
	max(ie_lib_sinal_vital),
	max(ie_lib_loco_regional),
	max(ie_lib_parecer_medico)
into STRICT	ie_lib_anamnese_w,
	ie_liberar_ganho_perda_w,
	ie_lib_sinal_vital_w,
	ie_lib_loco_regional_w,
	ie_lib_parecer_medico_w
from	parametro_medico
where	cd_estabelecimento = cd_estabelecimento_p;

if (coalesce(ie_lib_anamnese_w,'N') = 'N') then
	delete from pep_item_pendente where (nr_seq_anamnese IS NOT NULL AND nr_seq_anamnese::text <> '');
end if;

if (coalesce(ie_liberar_ganho_perda_w,'N') = 'N') then
	delete from pep_item_pendente where (nr_seq_perda_ganho IS NOT NULL AND nr_seq_perda_ganho::text <> '');
end if;

if (coalesce(ie_lib_sinal_vital_w,'N') = 'N') then
	delete from pep_item_pendente where (nr_seq_sinal_vital IS NOT NULL AND nr_seq_sinal_vital::text <> '');
end if;

if (coalesce(ie_lib_loco_regional_w,'N') = 'N') then
	delete from pep_item_pendente where (nr_seq_loco_reg IS NOT NULL AND nr_seq_loco_reg::text <> '');
end if;

if (coalesce(ie_lib_parecer_medico_w,'N') = 'N') then
	delete from pep_item_pendente where (nr_parecer IS NOT NULL AND nr_parecer::text <> '');
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
