SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_excluir_relatorio () AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_banda_w		bigint;

C01 CURSOR FOR
	SELECT	coalesce(nr_sequencia,0)
	from	banda_relatorio
	where	nr_seq_relatorio = nr_sequencia_w;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_sequencia_w
from 	relatorio
where	cd_classif_relat = 'W'
and	cd_relatorio = 1172
and	ds_titulo = 'teste';

if (nr_sequencia_w > 0) then
	begin

	open C01;
	loop
	fetch C01 into
		nr_seq_banda_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		delete	from banda_relat_campo
		where	nr_seq_banda = nr_seq_banda_w;

		end;
	end loop;
	close C01;

	delete from banda_relatorio
	where	nr_seq_relatorio = nr_sequencia_w;

	delete from relatorio
	where	nr_sequencia = nr_sequencia_w;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
