SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE baca_funcao_desenv_base_desenv () AS $body$
DECLARE


cd_cnpj_w	varchar(14);
cd_funcao_w	integer;

c01 CURSOR FOR
SELECT	e.cd_cgc
from	estabelecimento e
where	not exists (
		SELECT	1
		from	funcao_desenv f
		where	f.cd_cgc_cliente = e.cd_cgc);

c02 CURSOR FOR
SELECT	f.cd_funcao
from	funcao f
where	f.ie_situacao_swing in ('W','T')
and	not exists (
		SELECT	1
		from	funcao_desenv d
		where	d.cd_funcao = f.cd_funcao
		and	d.cd_cgc_cliente = cd_cnpj_w);


BEGIN
open c01;
loop
fetch c01 into cd_cnpj_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	open c02;
	loop
	fetch c02 into cd_funcao_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin
		insert into funcao_desenv(
			nr_sequencia,
			cd_funcao,
			cd_cgc_cliente,
			dt_atualizacao,
			nm_usuario)
		values (
			nextval('funcao_desenv_seq'),
			cd_funcao_w,
			cd_cnpj_w,
			clock_timestamp(),
			'Rafael');
		end;
	end loop;
	close c02;
	end;
end loop;
close c01;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
