CREATE OR REPLACE PROCEDURE baca_gerar_alta (cd_motivo_alta_p bigint) AS $body$
DECLARE

 
ds_erro_w		varchar(255);
nr_atendimento_w	bigint;

C01 CURSOR FOR 
	SELECT	nr_atendimento 
	from	atendimento_paciente 
	where	trunc(dt_entrada,'month') = to_date('01/04/2009','dd/mm/yyyy') 
	and	coalesce(dt_alta::text, '') = '';
			

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	ds_erro_w := Gerar_Estornar_Alta(nr_atendimento_w, 'A', 0, cd_motivo_alta_p, clock_timestamp(), 'BacaGerarAlta', ds_erro_w, null, null, 'Alta gerada pelo BACA');	
	 
	/*insert into logxxxxx_tasy (	DT_ATUALIZACAO, 
				NM_USUARIO   , 
				CD_LOG     , 
				DS_LOG     ) 
	values(	sysdate, 
		'BacaGerarAlta', 
		78925, 
		'Atendimento ' || nr_atendimento_w || ' Erro ' || ds_erro_w);*/
 
	 
	 
	end;
end loop;
close C01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
