CREATE OR REPLACE PROCEDURE baca_gerar_eis_conta_paciente () AS $body$
DECLARE

 
nr_interno_conta_w	bigint;

c01 CURSOR FOR 
SELECT 	a.nr_interno_conta 
from 	conta_paciente a 
where 	ie_status_acerto = 2 
and 	not exists (SELECT 1 
	from 	eis_conta_paciente b 
 	where 	a.nr_interno_conta = b.nr_interno_conta) 
and	coalesce(ie_cancelamento::text, '') = '';


BEGIN 
 
open c01;
loop 
fetch c01 into 
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	CALL gerar_eis_conta_paciente(nr_interno_conta_w);
	commit;
end loop;
close c01;
 
end;
